// Load a specific set of configurations\nfunction loadConfig($setName) {\n    $configPath = 'configs/config.ini';\n    $allConfigs = parse_ini_file($configPath, true);\n    \n    if (isset($allConfigs[$setName])) {\n        return $allConfigs[$setName];\n    } else {\n        throw new Exception("Configuration set '$setName' not found.");\n    }\n}\n\n// Example usage\ntry {\n    $configSet1 = loadConfig('set1');\n    print_r($configSet1);\n} catch (Exception $e) {\n    echo $e->getMessage();\n}