// Example of using preg_match_all to ensure $matches[1] is an array of strings\n$subject = "example string with words";\n$pattern = '/(\w+)/'; // Matches sequences of word characters\n\npreg_match_all($pattern, $subject, $matches);\n\nfor ($i = 0; $i < count($matches[1]); $i++) {\n    $val = $matches[1][$i];\n    echo "Value: $val\n"; // This will print each matched word on a new line\n}