<?php\nuse Doctrine\ORM\EntityManager;\nuse Doctrine\ORM\Tools\Setup;\n\n// Existing pg_connect parameters\n$host = 'localhost';\n$dbname = 'yourdbname';\n$user = 'youruser';\n$password = 'yourpassword';\n\n// Create a custom connection, wrapping around your current pg_connect setup\n$connectionOptions = [\n    'pdo' => new \PDO("pgsql:host=$host;port=5432;dbname=$dbname", $user, $password),\n];\n\n(isDevMode()) ?  Setup::createAnnotationMetadataConfiguration([__DIR__.'/src/Entity'], true) : \n                 Setup::createAnnotationMetadataConfiguration([__DIR__.'/src/Entity'], false);\n\n$config = Setup::createAnnotationMetadataConfiguration([__DIR__.'/src/Entity'], !isDevMode());\n$entityManager = EntityManager::create($connectionOptions, $config);