foreach (Conf::get_array() as $reflection_class) {\n    // Create a ReflectionClass instance from the class name in $reflection_class\n    $class = new ReflectionClass($reflection_class);\n    \n    // Check if the class has a constructor\n    if ($class->hasConstructor()) {\n        // Get the constructor method\n        $constructor = $class->getConstructor();\n        \n        // Create an instance of the class with parameters using reflection\n        $object = $class->newInstanceArgs([$param_1, $param_2]);\n    } else {\n        // If no constructor, instantiate without arguments\n        $object = $class->newInstanceWithoutConstructor();\n    }\n    \n    $objects[] = $object;\n}