// Example of a simple drush command file: deploy.drush.inc\n   \n   /**\n    * Implements hook_drush_command().\n    */\n   function deploy_drush_command() {\n     $items = [];\n   \n     $items['apply-fixtures'] = [\n       'description' => 'Apply fixtures to the database.',\n       'aliases' => ['afix'],\n     ];\n     \n     return $items;\n   }\n   \n   /**\n    * Command callback: Apply fixtures.\n    */\n   function drush_apply_fixtures() {\n     // Your code here to apply SQL and file fixtures\n     drush_sql_query("INSERT INTO table_name (column1, column2) VALUES ('value1', 'value2');");\n     drush_op('node_save', (object)['type' => 'article', 'title' => 'New Article']);\n   }