<?php\n// Database connection parameters\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "source_db";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Table name to select data from\n$sourceTable = 'your_source_table';\n$maxBatchSize = 100;\n\n// SQL query to select data\n$sql = "SELECT * FROM $sourceTable";\n$result = $conn->query($sql);\n\nif ($result && $result->num_rows > 0) {\n    // Initialize variables\n    $batchCount = 0;\n    $valuesBatch = [];\n    \n    // Output file path\n    $outputFilePath = 'insert_script.sql';\n\n    // Open the output file\n    $fileHandle = fopen($outputFilePath, 'w');\n\n    // Loop through each row of data\n    while ($row = $result->fetch_assoc()) {\n        // Generate a value string for this row\n        $valueString = "('" . implode("','", array_map('mysqli_real_escape_string', [$conn], $row)) . "')";\n\n        // Add the value string to the batch\n        $valuesBatch[] = $valueString;\n        \n        // If we've reached max batch size, write the insert statement and reset the batch\n        if (count($valuesBatch) >= $maxBatchSize) {\n            fwrite($fileHandle, "INSERT INTO your_target_table VALUES " . implode(',', $valuesBatch) . ";\n");\n            $valuesBatch = [];\n        }\n    }\n\n    // Write any remaining values if there are less than the max batch size\n    if (!empty($valuesBatch)) {\n        fwrite($fileHandle, "INSERT INTO your_target_table VALUES " . implode(',', $valuesBatch) . ";\n");\n    }\n\n    // Close the file handle\n    fclose($fileHandle);\n\n    echo "Data exported to $outputFilePath\n";\n} else {\n    echo "No data found or query failed.\n";\n}\n\n// Close the database connection\n$conn->close();\n?>