<?php\n$postedDate = '2023-10-01'; // Example date\n$date = new DateTime($postedDate);\n\n// Add 7 days\n$datePlus7Days = clone $date;\n$datePlus7Days->modify('+7 days');\necho "Date after 7 days: " . $datePlus7Days->format('m/d/Y') . "\n";\n\n// Add 15 days\n$datePlus15Days = clone $date;\n$datePlus15Days->modify('+15 days');\necho "Date after 15 days: " . $datePlus15Days->format('m/d/Y') . "\n";\n\n// Add 1 month\n$datePlus1Month = clone $date;\n$datePlus1Month->modify('+1 month');\necho "Date after 1 month: " . $datePlus1Month->format('m/d/Y') . "\n";\n\n// Add 2 months\n$datePlus2Months = clone $date;\n$datePlus2Months->modify('+2 months');\necho "Date after 2 months: " . $datePlus2Months->format('m/d/Y') . "\n";\n?>