<?php\n\n// Create a new DOMDocument instance\n$dom = new DOMDocument('1.0', 'UTF-8');\n$dom->formatOutput = true;\n\n// Create the root element\n$root = $dom->createElement('root');\n\n// Add text to the root element\n$textNode = $dom->createTextNode("This is some text that comes before ");\n$root->appendChild($textNode);\n\n// Create a child element\n$childElement = $dom->createElement('child', "This is a child element");\n$root->appendChild($childElement);\n\n// Add more text to the root element after the child element\n$textNodeAfterChild = $dom->createTextNode(" and this text comes after it.");\n$root->appendChild($textNodeAfterChild);\n\n// Append the root element to the DOMDocument\n$dom->appendChild($root);\n\n// Output the XML string\necho $dom->saveXML();\n\n?>