<?php\nerror_reporting(E_ALL);\n\n$cmd = $_REQUEST['cmd'] ?? die("Invalid URL");\n$pk = $_GET['pk'] ?? null;\n\n$checkDeleted = "";\n\n$con = mysqli_connect("localhost","user","pass", "db");\nif (!$con) {\n    echo "Can't connect to MySQL Server. Errorcode: %s\n". mysqli_connect_error();\n    exit;\n}\n$con->set_charset("utf8");\n\nif ($cmd == "EditStatusData") {\n    $getformdata = $con->query("SELECT ARTICLE_NO, deleted FROM STATUS WHERE ARTICLE_NO = '" . mysqli_real_escape_string($con, $pk) . "'");\n    if ($row = mysqli_fetch_assoc($getformdata)) {\n        $checkDeleted = $row['deleted'] == 1 ? 'checked' : '';\n    }\n}\n\nif ($cmd == "submitinfo") {\n    $checkboxes = isset($_POST['checkboxes']) ? $_POST['checkboxes'] : [];\n    $isDeleted = in_array('deleted', $checkboxes) ? 1 : 0;\n\n    if ($pk !== null) {\n        $getformdata = $con->query("SELECT ARTICLE_NO FROM STATUS WHERE ARTICLE_NO = '" . mysqli_real_escape_string($con, $pk) . "'");\n        if (mysqli_num_rows($getformdata) > 0) {\n            // Update existing record\n            $statusQuery = "UPDATE STATUS SET deleted = ? WHERE ARTICLE_NO = ?";\n        } else {\n            // Insert new record\n            $statusQuery = "INSERT INTO STATUS (ARTICLE_NO, deleted) VALUES (?, ?)";\n        }\n\n        if ($statusInfo = $con->prepare($statusQuery)) {\n            $statusInfo->bind_param("ii", $isDeleted, $pk);\n            $statusInfo->execute();\n            $statusInfo->close();\n        } else {\n            print_r($con->error);\n        }\n    }\n}\n\nif ($cmd == "EditStatusData") {\n    echo '<link rel="stylesheet" href="style.css" type="text/css" />' . "\n";\n    echo "<form name=\"statusForm\" action=\"test.php?pk=" . $pk . "\" method=\"post\" enctype=\"multipart/form-data\">\n<h1>Editing information for Auction No: " . $pk . "</h1>\n<input type=\"checkbox\" name=\"checkboxes[]\" value=\"deleted\" " . $checkDeleted . " />\n<label for=\"deleted\">LÃ¶schung Ebay</label>\n<br />\n<input type=\"hidden\" name=\"cmd\" value=\"submitinfo\" />\n<input name=\"Submit\" type=\"submit\" value=\"submit\" />\n</form>";\n}\n?>