function findSecondToLastOccurrence($string, $char) {\n    // Find the position of the last occurrence\n    $lastPos = strrpos($string, $char);\n    \n    if ($lastPos === false || $lastPos === 0) {\n        return false; // No second-to-last occurrence can be found\n    }\n    \n    // Find the second-to-last occurrence by searching before the last position\n    $secondLastPos = strrpos(substr($string, 0, $lastPos), $char);\n    \n    if ($secondLastPos === false) {\n        return false; // No second-to-last occurrence can be found\n    }\n    \n    return $secondLastPos;\n}\n\n// Example usage:\n$string = "example string with some e's";\n$char = 'e';\n$result = findSecondToLastOccurrence($string, $char);\nif ($result !== false) {\n    echo "The second-to-last occurrence of '$char' is at index: $result";\n} else {\n    echo "'$char' does not occur a second-to-last time in the string.";\n}