// Assuming $result is your MySQL query result resource or PDO statement object\n\n$result = $mysqli->query("YOUR QUERY HERE");\n\n$data = []; // Array to hold the results\n\nwhile ($row = $result->fetch_object()) {\n    $data[] = $row;\n}\n\n$serializedData = serialize($data); // Serialize array of objects\n\nfile_put_contents('cache_file.php', $serializedData); // Write serialized data to file