<?php\n$imagePath = '/path/to/your/image.jpg'; // Path to the original image\n$resizedImagePath = '/path/to/save/resized_image.jpg'; // Desired path for the resized image\n\n// Create a new Imagick object from the file\n$imagick = new \Imagick(realpath($imagePath));\n\n// Set the compression quality if needed (100 is max quality)\n$imagick->setImageCompressionQuality(100);\n\n// Resize the image using MagickTransformImage or directly with resizeImage\n// Here we use resizeImage for simplicity and better control over resizing\n$imagick->resizeImage(\n    660, // New width\n    500, // New height\n    \Imagick::FILTER_LANCZOS, // Filter\n    1 // Blur factor (1 means no blur)\n);\n\n// Write the transformed image to a file\n$imagick->writeImage(realpath($resizedImagePath));\n\n// Clean up resources\n$imagick->clear();\n$imagick->destroy();\n\necho "Resized image saved to: " . $resizedImagePath;\n?>