<?php\n\n// Database connection\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get all text from the BLOB column\n$query = "SELECT data_column FROM your_table";\n$result = $mysqli->query($query);\n\n$words = [];\n\nwhile ($row = $result->fetch_assoc()) {\n    // Convert blob to string and split into words\n    $text = iconv("UTF-8", "UTF-8//IGNORE", $row['data_column']);\n    preg_match_all('/\b\w+\b/', strtolower($text), $matches);\n    \n    // Count word occurrences\n    foreach ($matches[0] as $word) {\n        if (!in_array($word, ['but', 'also'])) {  // Exclude common stopwords\n            isset($words[$word]) ? $words[$word]++ : $words[$word] = 1;\n        }\n    }\n}\n\n// Sort words by frequency in descending order\narsort($words);\n\n// Display top N words\n$topWords = array_slice($words, 0, 2);  // Change 2 to the number of top words you want\nforeach ($topWords as $word => $count) {\n    echo "$word: $count\n";\n}\n\n$mysqli->close();\n\n?>