<?php \n$my_query = new WP_Query('category_name=posts&posts_per_page=1'); \nif ($my_query->have_posts()) : \n    while ($my_query->have_posts()) : $my_query->the_post(); \n        // Sanitize and check for empty value before outputting\n        $qanda_value = get_post_meta(get_the_ID(), "qanda", true);\n        if (!empty($qanda_value)) {\n            echo esc_html($qanda_value); // Use esc_html() to prevent XSS\n        }\n    endwhile; \nendif; \nwp_reset_postdata(); // Reset the global post data\n?>