<?php\nsession_start();\n\nif(!isset($_SESSION['username'])) {\n    die('User not logged in');\n}\n\n$message = $_POST['message'];\n$username = $_SESSION['username'];\n\n// Sanitize and insert message into DB\nrequire_once 'database.php'; // Assume this file handles your DB connection\n\n$stmt = $pdo->prepare("INSERT INTO chat_messages (username, content) VALUES (?, ?)");\n$stmt->execute([$username, $message]);\n\necho "Message added!";\n?>