<?php\n// Encoding an associative array into JSON\n$data = array("name" => "John", "age" => 30, "city" => "New York");\n$jsonString = json_encode($data);\necho $jsonString; // Output: {"name":"John","age":30,"city":"New York"}\n\n// Decoding a JSON string into an associative array\n$jsonReceived = '{"name":"John", "age":30, "city":"New York"}';\n$dataDecoded = json_decode($jsonReceived, true);\necho $dataDecoded['name']; // Output: John\n?>