<?php\nrequire('Smarty.class.php');\n$smarty = new Smarty();\n\n// Set directories properly if needed\n$smarty->setTemplateDir('/path/to/templates');\n$smarty->setCompileDir('/path/to/templates_c');\n$smarty->setCacheDir('/path/to/cache');\n\n// Enable caching\n$smarty->caching = Smarty::CACHING_LIFETIME_CURRENT;\n\n// Assign the module dynamically based on query parameter\n$module = isset($_GET['module']) ? $_GET['module'] : 'index';\n$page_center = $module . '.tpl';\n\n// Use the module name as a cache id to differentiate caches\n$smarty->display('index.tpl', $module);\n\n?>