function processFraudForm($crm_number, $datacash_ref, $amount, $sales_date, $domain, $agent_added) {\n    global $connection;\n\n    // Prepare the statement\n    $stmt = mysql_prepare($connection, "INSERT INTO fraud_report (crm_number, datacash_ref, amount, sales_date, domain, date_added, agent_added) VALUES (?, ?, ?, ?, ?, NOW(), ?)");\n\n    // Bind parameters\n    mysql_stmt_bind_param($stmt, 'sssss', $crm_number, $datacash_ref, $amount, $sales_date, $domain, $agent_added);\n\n    // Execute the statement\n    if (mysql_stmt_execute($stmt)) {\n        $outcome = "<div id=\"success\">Emails sent and database updated.</div>";\n    } else {\n        $outcome = "<div id=\"error\">Something went wrong!</div>";\n    }\n\n    // Close the statement\n    mysql_stmt_close($stmt);\n\n    return $outcome;\n}