<?php\n\nheader('Content-Type: application/json');\n\n$totalRecords = 100;\n$processed = 0;\n\nwhile ($processed < $totalRecords) {\n    // Simulate processing a record\n    usleep(50000); // Sleep for 50ms\n\n    $processed++;\n\n    echo json_encode(['status' => 'processing', 'progress' => $processed, 'total' => $totalRecords]);\n    flush();\n}\n\necho json_encode(['status' => 'complete']);\nflush();\n\n?>