foreach ($interestingBoxes as $box) {\n    // Find the 'interestingdetails' div inside each 'interestingbox'\n    $detailNodes = $box->getElementsByTagName('div');\n\n    foreach ($detailNodes as $(detailNode) {\n        if ($detailNode->getAttribute('class') === 'txtnormal') {\n            foreach ($detailNode->getElementsByTagName('div') as $contentDiv) {\n                echo trim($contentDiv->nodeValue) . "\n";\n            }\n        }\n    }\n}