<?php\n\n// Load the HTML file\n$htmlContent = file_get_contents('yourfile.html');\n\n// Create a DOMDocument instance\n$dom = new DOMDocument();\n\n// Handle possible errors in parsing malformed HTML\nlibxml_use_internal_errors(true);\n$dom->loadHTML($htmlContent);\nlibxml_clear_errors();\n\n// Get all 'div' elements with the id 'interestingbox'\n$xpath = new DOMXPath($dom);\n$interestingBoxes = $xpath->query("//div[@id='interestingbox']");\n\nforeach ($interestingBoxes as $box) {\n    // Optionally, get more specific details inside\n    $detailsBox = $box->querySelector('div#interestingdetails');\n\n    if ($detailsBox) {\n        foreach ($detailsBox->childNodes as $childNode) {\n            if ($childNode->nodeName === 'div') {\n                echo trim($childNode->nodeValue) . "\n";\n            }\n        }\n    }\n}