function separateWords($string) {\n    // This regex matches an uppercase letter that is not at the start and is preceded by a lowercase letter.\n    return preg_replace('/(?<=[a-z])([A-Z])/', ' $1', $string);\n}\n\n// Example usage:\n$inputString = "ThisWasCool to visit you again";\n$separatedString = separateWords($inputString);\necho $separatedString; // Outputs: This Was Cool to visit you agai