<?php\n$database_env = getenv('DATABASE_ENV') ?: 'live'; // Default to live if not set\n\nif ($database_env === 'testing') {\n    $database_config = [\n        'host' => 'staging-db-host',\n        'dbname' => 'staging_db_name',\n        'user' => 'staging_user',\n        'password' => 'staging_password',\n    ];\n} else { // live environment\n    $database_config = [\n        'host' => 'live-db-host',\n        'dbname' => 'live_db_name',\n        'user' => 'live_user',\n        'password' => 'live_password',\n    ];\n}\n\n// Now you can use $database_config to establish your database connection.