function generateAccNo() {\n    global $dbConnection; // Assuming you have a database connection established\n\n    while (true) {\n        // Generate an account number between 100,000,000 and 999,999,999\n        $accountNumber = mt_rand(100000000, 999999999);\n\n        // Check if the account number already exists in the database\n        $query = "SELECT COUNT(*) AS count FROM accounts WHERE acc_number = ?";\n        $stmt = $dbConnection->prepare($query);\n        $stmt->bind_param("i", $accountNumber);\n        $stmt->execute();\n        $result = $stmt->get_result()->fetch_assoc();\n\n        if ($result['count'] == 0) {\n            return $accountNumber; // Return the unique account number\n        }\n    }\n}