<?php\nsession_start(); // Always start session at the beginning\n\nif (isset($_GET['destroy'])) {\n    session_destroy();\n    session_start(); // Restart session to ensure everything is reset properly\n    $_SESSION['counter'] = 0; // Reset counter after destroying session\n} \n\n// Initialize the counter if not set\nif (!isset($_SESSION['counter'])) {\n    $_SESSION['counter'] = 1;\n} else {\n    $_SESSION['counter']++;\n}\n\n?>\n\n<html>\n<head>\n    <title>Session test</title>\n</head>\n<body>\n<?php\nif (isset($_GET['destroy'])) {\n    echo "Destroy set<br>";\n}\n\necho "You have visited this page " . $_SESSION['counter'] . " times.<br>";\necho "I am tracking you using the session id " . session_id() . "<br>";\necho "Click <a href=\"" . $_SERVER['PHP_SELF'] . "?destroy=1\">here</a> to destroy the session.";\n?>\n</body>\n</html>