$thisTime = gmmktime(0, 0, 0);\n$sel = ""; // Initialize $sel to an empty string\n\nfor($i=0; $i<=95; $i++) {\n    $perfTimeNumber = ($i+1);\n    $perfTimestamp = $thisTime;\n    $perfTime = date("H:i", $perfTimestamp);\n\n    if ($perfTime == '19:30') { // Use double equals for comparison\n        $sel = "selected";\n    } else {\n        $sel = ""; // Reset $sel if it's not 19:30\n    }\n\n    echo '<option value="'. $perfTimeNumber .'" selected="'.$sel.'">' .$perfTime .'</option>';\n\n    $thisTime = $thisTime+(15*60);\n}