<?php\n$page = filter_input(INPUT_GET, 'page', FILTER_SANITIZE_URL);\n\n// Check if the URL is valid\nif (filter_var($page, FILTER_VALIDATE_URL) !== false) {\n    // Optionally, check that the URL points to your own domain only\n    $allowed_domains = ['example.com'];\n    $parsed_url = parse_url($page);\n    if (in_array($parsed_url['host'], $allowed_domains)) {\n        header("Location: " . $page);\n        exit;\n    }\n} else {\n    // Handle invalid URL, e.g., log or redirect to home page\n    header("Location: /"); // Redirect to a default safe location\n    exit;\n}\n?>