function getScreen($url, $size = 'big') {\n    if (!$url) { return ""; }\n    \n    $size = ($size === 'small') ? '1' : '0';\n    preg_match('/[?&]v=([^&#]*)/', $url, $matches);\n    $vid = isset($matches[1]) ? $matches[1] : $url;\n    \n    return "https://img.youtube.com/vi/{$vid}/{$size}.jpg";\n}\n\n// Example usage:\n$imgUrlBig = getScreen("uVLQhRiEXZs"); // default is big\n$imgUrlSmall = getScreen("uVLQhRiEXZs", 'small');\necho $imgUrlBig;\necho "\n";\necho $imgUrlSmall;