<?php\n\nfunction extractImageAttributes($html) {\n    $dom = new DOMDocument();\n    // Suppress errors due to malformed HTML\n    @$dom->loadHTML($html);\n\n    $images = [];\n    foreach ($dom->getElementsByTagName('img') as $image) {\n        $src = $image->getAttribute('src');\n        $title = $image->getAttribute('title');\n        $alt = $image->getAttribute('alt');\n\n        $images[] = [\n            'src' => $src,\n            'title' => $title,\n            'alt' => $alt\n        ];\n    }\n\n    return $images;\n}\n\n// Example usage:\n$htmlContent = '<img src="/image/fluffybunny.jpg" title="Harvey the bunny" alt="a cute little fluffy bunny" />';\n$imageData = extractImageAttributes($htmlContent);\n\nprint_r($imageData);\n?>