function top_menu_logged_in_sub_menu($user, $page = 'main') {\n    $sub_menu_html = '<div id="sub_nav"><ul>';\n\n    $pages = [\n        'main' => 'My Admin',\n        'myProjects' => 'My Projects',\n        'insertProject' => 'Post a Project',\n        'myAccount' => 'My Account'\n    ];\n\n    foreach ($pages as $pageId => $label) {\n        $isActive = $page === $pageId ? '_active' : '';\n        $sub_menu_html .= '\n            <li><img src="images/nav_2_lev_tab' . (($page === $pageId) ? '_Hi' : '') . '_L.gif" alt="" /></li>\n            <li class="sub_link' . $isActive . '"><span><a href="' . $pageId . '.php">' . htmlspecialchars($label) . '</a></span></li>\n            <li><img src="images/nav_2_lev_tab' . (($page === $pageId) ? '_Hi' : '') . '_R.gif" alt="" /></li>\n        ';\n    }\n\n    $sub_menu_html .= '\n        </ul>\n        <div class="user_id">\n            Welcome, ' . htmlspecialchars($user) . '! &nbsp;&nbsp;<span class="sign_out"><a href="http://www.example.org/login.php?action=logout">Sign Out</a></span>&nbsp;&nbsp;\n        </div>\n    </div>';\n\n    return $sub_menu_html;\n}