<?php\nsession_start();\n\n$servername = "localhost";\n$username = "your_db_username";\n$password = "your_db_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $userInputUsername = $_POST['username'];\n    $userInputPassword = $_POST['passe'];\n\n    // Prepare and bind\n    $stmt = $conn->prepare("SELECT passwd, power FROM user WHERE username=?");\n    $stmt->bind_param("s", $userInputUsername);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    if ($row = $result->fetch_assoc()) {\n        // Verify the password (assuming passwords are hashed)\n        if (password_verify($userInputPassword, $row['passwd'])) {\n            $_SESSION['MM_Username'] = $userInputUsername;\n            $_SESSION['MM_UserGroup'] = $row['power'];\n            header("Location: administrador.php");\n            exit();\n        } else {\n            echo "Invalid username or password.";\n        }\n    } else {\n        echo "User not found.";\n    }\n\n    $stmt->close();\n}\n\n$conn->close();\n?>\n\n<form method="post" action="">\n    <label for="username">Username:</label>\n    <input type="text" id="username" name="username"><br><br>\n    <label for="password">Password:</label>\n    <input type="password" id="password" name="passe"><br><br>\n    <input type="submit" value="Login">\n</form>