class SomeController extends Zend_Controller_Action\n{\n    public function init()\n    {\n        $request = $this->getRequest();\n        $frontController = Zend_Controller_Front::getInstance();\n        $router = $frontController->getRouter();\n\n        // Get current category from request or set a default\n        $category = $request->getParam('category', 'defaultCategory');\n\n        // Define the route with dynamic default value\n        $categoryRoute = new Zend_Controller_Router_Route(\n            'category/:category/:controller/:action/*',\n            array(\n                'controller' => 'index',\n                'action'     => 'index',\n                'category'   => $category // Set category dynamically from request\n            )\n        );\n\n        // Add or override the route\n        $router->addRoute('category', $categoryRoute);\n    }\n\n    public function someAction()\n    {\n        // Your action logic here\n    }\n}