function getCurrentDirectoryUrl() {\n    // Determine if we are using HTTPS.\n    $protocol = (!empty($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off' || \n                $_SERVER['SERVER_PORT'] == 443) ? "https://" : "http://";\n\n    // Get the server name and script name\n    $host = $_SERVER['HTTP_HOST'];\n    $scriptName = $_SERVER['SCRIPT_NAME'];\n\n    // Remove the file name to get the directory path\n    $directoryPath = dirname($scriptName);\n\n    // Construct full URL of the directory\n    $directoryUrl = $protocol . $host . $directoryPath;\n\n    return $directoryUrl;\n}\n\necho getCurrentDirectoryUrl();