class AppController extends Controller {\n    public function beforeFilter(Event $event) {\n        parent::beforeFilter($event);\n        self::initializeData(); // Static method to initialize data\n    }\n\n    public static function initializeData() {\n        // Your initialization logic here\n        CakeSession::write('SomeKey', 'SomeValue');\n        // ... other initializations\n    }\n}\n\nclass AppError extends ErrorHandler {\n    public function _outputMessage($template) {\n        AppController::initializeData(); // Call the shared initialization logic\n        parent::_outputMessage($template);\n    }\n}