function customFunction($args = array()) {\n    // Set default arguments and merge with user-supplied arguments\n    $defaults = array(\n        'key1' => 'default_value1',\n        'key2' => 'default_value2'\n    );\n\n    // Merge defaults and arguments\n    $args = wp_parse_args($args, $defaults);\n\n    // Access your variables via keys\n    $key1Value = $args['key1'];\n    $key2Value = $args['key2'];\n\n    // Example usage\n    echo "Key 1 value: " . htmlspecialchars($key1Value) . "\n";\n    echo "Key 2 value: " . htmlspecialchars($key2Value);\n}\n\n// Usage of the function with key-value pair\ncustomFunction(array('key1' => 'value1', 'key2' => 'value2'));