<?php\n// Set the path to store the cache file\n$cacheFile = 'last_update.cache';\n\n// Check if the cache file exists and is not older than 15 minutes\nif (file_exists($cacheFile) && (time() - filemtime($cacheFile) < 900)) {\n    // Read the last update time from cache\n    $lastUpdate = unserialize(file_get_contents($cacheFile));\n} else {\n    // Connect to your database and get the last updated date\n    $mysqli = new mysqli("your_host", "your_user", "your_password", "your_database");\n    \n    if ($result = $mysqli->query("SELECT MAX(last_updated) AS last_update_time FROM your_table_name")) {\n        $row = $result->fetch_assoc();\n        $lastUpdate = $row['last_update_time'];\n        \n        // Save the last update time to cache\n        file_put_contents($cacheFile, serialize($lastUpdate));\n    }\n    \n    $mysqli->close();\n}\n\n// Display the last update time in your footer\necho "Last updated: " . date('m/d/Y H:i:s', strtotime($lastUpdate));\n?>