<?php\n// Database connection setup (example)\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to select all records from your table\n$query = "SELECT * FROM your_table";\n$result = $mysqli->query($query);\n\n$formattedResults = [];\nwhile ($row = $result->fetch_assoc()) {\n    foreach ($row as $key => $value) {\n        // Check if the field is a timestamp (you might want to change this check)\n        if (strtotime($value) !== false) {\n            $row[$key] = date('m/d/y', strtotime($value));\n        }\n    }\n    $formattedResults[] = $row;\n}\n\n// Now $formattedResults contains all records with date fields formatted as m/d/y\nprint_r($formattedResults);\n\n$result->free();\n$mysqli->close();\n?>