<?php\nheader('Content-Type: image/png');\n\n// Initialize paths to images\n$images = ['path/to/image1.png', 'path/to/image2.png'];\n\n// Create an array to hold GD image resources\n$imageResources = [];\n\n// Get the dimensions needed for our final combined image\n$totalWidth = 0;\n$maxHeight = 0;\nforeach ($images as $image) {\n    list($width, $height) = getimagesize($image);\n    $totalWidth += $width;\n    if ($height > $maxHeight) {\n        $maxHeight = $height;\n    }\n    $imageResources[] = imagecreatefrompng($image); // For PNG images\n}\n\n// Create the destination image\n$finalImage = imagecreatetruecolor($totalWidth, $maxHeight);\n$currentColorXPosition = 0;\n\n// Copy each resource into our final image\nforeach ($imageResources as $resource) {\n    list($width, $height) = getimagesize($image); // Reuse the images array for simplicity\n    imagecopyresampled($finalImage, $resource, $currentColorXPosition, 0, 0, 0, $width, $height, $width, $height);\n    $currentColorXPosition += $width;\n}\n\n// Output image as PNG and free up resources\nimagepng($finalImage);\nforeach ($imageResources as $resource) {\n    imagedestroy($resource);\n}\nimagedestroy($finalImage);\n?>