<?php\n// Database connection code here\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "myDB";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT order_id, customer_name, amount FROM open_orders";\n$result = $conn->query($sql);\n\necho "<table>";\necho "<tr><th>Order ID</th><th>Customer Name</th><th>Amount</th></tr>";\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "<tr><td>" . $row["order_id"] . "</td><td>" . $row["customer_name"] . "</td><td>" . $row["amount"] . "</td></tr>";\n    }\n} else {\n    echo "0 results";\n}\n\necho "</table>";\n\n$conn->close();\n?>