function checkVariable($variable) {\n    switch ($variable) {\n        case 'value1':\n            return 'Response for value1';\n        case 'value2':\n            return 'Response for value2';\n        case 'value3':\n            return 'Response for value3';\n        // Add more cases as needed\n        default:\n            return 'Default response';\n    }\n}\n\n// Example usage\n$var = 'value1';\necho checkVariable($var);