<?php\n// Load the RTF file\n$rtfFilePath = "mark.rtf";\n$fileContent = file_get_contents($rtfFilePath);\n\n// Create a COM object to interact with Microsoft Word\ntry {\n    $word = new COM("Word.Application") or die("Could not create Word Object");\n} catch (Exception $e) {\n    die('Error: ' . $e->getMessage());\n}\n\n// Set Word to be invisible\n$word->Visible = false;\n\n// Create a new document and insert the RTF content\n$doc = $word->Documents->Add();\n$range = $doc->Range(0, 0);\n$range->LoadFromText($fileContent);\n\n// Convert the document to HTML format\n$tempHTMLPath = tempnam(sys_get_temp_dir(), "rtf_to_html") . '.html';\n$doc->SaveAs($tempHTMLPath, 8); // wdFormatFilteredHTML\n\n// Get the converted HTML content\n$htmlContent = file_get_contents($tempHTMLPath);\n\n// Close Word and clean up\n$word->Documents[1]->Close(false);\nunset($word);\n\n// Display the HTML content in the browser\necho $htmlContent;\n\n// Optionally, remove the temporary HTML file\nunlink($tempHTMLPath);\n?>