<?php\n\n$dsn = 'mysql:host=localhost;dbname=testdb;charset=utf8mb4';\n$username = 'yourUsername';\n$password = 'yourPassword';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    // Set the PDO error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare a multi-query statement. \n    // Note: This example assumes you are certain this is safe.\n    $sql = "SELECT id, name FROM users WHERE age > 30; "\n         . "SELECT department_id, manager_name FROM departments;";\n\n    // Execute the multi-queries\n    $stmt = $pdo->query($sql);\n\n    do {\n        if ($result = $stmt->fetchAll(PDO::FETCH_ASSOC)) {\n            print_r($result);\n        }\n    } while ($stmt->nextRowset());\n\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n$pdo = null;\n?>