function extractMatch($pattern, $subject) {\n    if (preg_match($pattern, $subject, $matches)) {\n        return $matches[0];\n    }\n    return false;\n}\n\n$myString = "This is some text, the email is here example@example.com, and other things over here";\n$emailPattern = '/^\w+[\w-.]*@\w+((-\w+)|(\w*))\.[a-z]{2,3}$/';\n\n// Since the pattern above matches an entire line, we need to modify it for a string search\n$emailPatternForStringSearch = '/\b\w+[\w-.]*@\w+((-\w+)|(\w*))\.[a-z]{2,3}\b/';\n\n$result = extractMatch($emailPatternForStringSearch, $myString);\necho var_export($result, true); // Outputs: 'example@example.com'