function checkVariable($variable) {\n    // Define responses in an associative array\n    $responses = [\n        'value1' => 'Response for value1',\n        'value2' => 'Response for value2',\n        'value3' => 'Response for value3',\n        // Add more values and responses as needed\n    ];\n\n    // Check if the variable exists in the array\n    if (array_key_exists($variable, $responses)) {\n        return $responses[$variable];\n    } else {\n        return 'Default response';\n    }\n}\n\n// Example usage\n$var = 'value1';\necho checkVariable($var);