<?php\n$host = 'your_host'; // usually localhost\n$dbname = 'your_database_name';\n$user = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname;charset=utf8", $user, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Create table\n    $createTableQuery = "CREATE TABLE IF NOT EXISTS `your_table_name` (\n        `id` int(25) NOT NULL AUTO_INCREMENT,\n        `column1` varchar(250) COLLATE utf8_unicode_ci DEFAULT NULL,\n        `column2` int(10) DEFAULT NULL,\n        PRIMARY KEY (`id`)\n    ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;";\n    \n    $pdo->exec($createTableQuery);\n\n    // Insert data\n    $insertDataQuery = "INSERT INTO `your_table_name` (`column1`, `column2`) VALUES (:value1, :value2);";\n    $stmt = $pdo->prepare($insertDataQuery);\n    $stmt->execute([':value1' => 'Value for column 1', ':value2' => 'Value for column 2']);\n\n} catch (PDOException $e) {\n    echo "Connection failed: " . $e->getMessage();\n}\n?>