class Home extends MY_Controller {\n\n    public function index() {\n        // Load widgets for Home Page\n        $data['feature'] = $this->load->view('widgets/home_feature', '', true);\n        $data['login'] = $this->load->view('widgets/login', '', true);\n        $data['calendar'] = $this->load->view('widgets/calendar_home', '', true);\n        $data['photos'] = $this->load->view('widgets/photos_scroll', '', true);\n        $data['tags'] = $this->load->view('widgets/tags_view', '', true);\n        $data['spotlight'] = $this->load->view('widgets/spotlight', '', true);\n        $data['recent'] = $this->load->view('widgets/activityfeed', '', true);\n\n        // Set the title and meta tags\n        $data['title'] = 'Philly2Night.com';\n        $data['MetaDesc'] = 'Cities2Night new social network';\n        $data['MetaKeywords'] = 'Social, Party, Plan, Events';\n\n        // Render the page\n        $this->render_page('home_view', $data);\n    }\n\n    public function about() {\n        // Set the title and meta tags\n        $data['title'] = 'About Us - Philly2Night.com';\n        $data['MetaDesc'] = 'Learn more about Cities2Night social network.';\n        $data['MetaKeywords'] = 'About, Cities2Night, Social Network';\n\n        // Render the page\n        $this->render_page('about_view', $data);\n    }\n}