class MY_Controller extends CI_Controller {\n\n    protected function render_page($view, $data = array()) {\n        // Load Includes for all pages\n        $data['header'] = $this->load->view('includes/header', '', true);\n        $data['scripts'] = $this->load->view('includes/scripts', '', true);\n        $data['navigation'] = $this->load->view('includes/navigation', '', true);\n        $data['footer'] = $this->load->view('includes/footer', '', true);\n\n        // Load the actual view\n        $this->load->vars($data);\n        $this->load->view("pages/$view");\n    }\n}