class Hello {\n    // Private constructor to prevent instantiation\n    private function __construct() {}\n\n    // Static property\n    private static $greeting = 'Hello';\n\n    // Static method initializer\n    public static function init() {\n        self::$greeting .= ' There!';\n    }\n\n    // Static method to greet\n    public static function greet() {\n        self::init(); // Optional: Initialize internally if needed\n        echo self::$greeting;\n    }\n}\n\n// Call the static method without instantiation\nHello::greet(); // Outputs: Hello There!