<?php\n\n   /**\n    * Implements hook_form_alter().\n    */\n   function siteinfo_form_alter(&$form, &$form_state, $form_id) {\n     if ($form_id == 'system_site_information_settings') {\n       // Adding a text field.\n       $form['custom_field'] = array(\n         '#type' => 'textfield',\n         '#title' => t('Custom Field'),\n         '#default_value' => variable_get('custom_field', ''),\n         '#description' => t('Description of the custom field.'),\n         '#required' => FALSE, // Set to TRUE if required.\n       );\n\n       // Adding a submit handler if you need additional processing on form submission.\n       $form['#submit'][] = 'siteinfo_submit';\n     }\n   }\n\n   /**\n    * Custom submit handler for adding our values to variables.\n    */\n   function siteinfo_submit(&$form, &$form_state) {\n     variable_set('custom_field', $form_state['values']['custom_field']);\n   }