function get_date_offset($start_date, $end_date, $format) {\n    // Create DateTime objects from the provided dates using the specified format\n    $date_start = DateTime::createFromFormat($format, $start_date);\n    $date_end = DateTime::createFromFormat($format, $end_date);\n\n    // Check if date creation was successful\n    if (!$date_start || !$date_end) {\n        throw new Exception("Invalid date format provided.");\n    }\n\n    // Calculate the difference between the two dates\n    $interval = $date_start->diff($date_end);\n\n    // Return the total number of days in the interval\n    return $interval->days;\n}\n\n// Example usage:\ntry {\n    echo get_date_offset('01/01/2023', '12/31/2023', 'm/d/Y'); // Outputs: 364\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}